	},
	{
		name: 'Vishwakaran Belaganti',
		rNumber: '11910473',
		term: 'Fall 2025',
		classes: [
			{
				code: 'CS 3364',
				name: 'Design and Analysis of Algorithms',
				instructor: 'Uma Maheswari Chinta',
				credits: 3.0,
				lecture: '002 (CRN 49808)',
				location: 'Electrical Engineering 217',
				format: 'Face-to-Face',
				days: 'Monday, Wednesday, Friday',
				time: '4:00 PM – 4:50 PM',
			},
			{
				code: 'COMS 2358',
