import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Helper to get today's classes for all students
interface Advisor {
  name: string;
  role: string;
  email: string;
  phone?: string;
}
interface Instructor {
  name: string;
  course: string;
  email: string;
  phone?: string;
}
interface Student {
  name: string;
  rNumber: string;
  term: string;
  classes: Array<{
    code: string;
    name: string;
    instructor: string;
    credits: number;
    lecture?: string;
    location: string;
    days: string;
    time: string;
    format?: string;
  }>;
  totalCredits: number;
  advisors: Advisor[];
  instructors: Instructor[];
  projects?: string;
  financialAidContact?: string;
}
interface TodaysClass {
  student: string;
  code: string;
  name: string;
  instructor: string;
  location: string;
  time: string;
}
function getTodaysClasses(students: Student[]): TodaysClass[] {
  const daysMap: { [key: number]: string } = {
    0: 'Sunday',
    1: 'Monday',
    2: 'Tuesday',
    3: 'Wednesday',
    4: 'Thursday',
    5: 'Friday',
    6: 'Saturday',
  };
  const today = new Date();
  const todayStr = daysMap[today.getDay()];
  const result: TodaysClass[] = [];
  students.forEach((student) => {
    student.classes.forEach((cls) => {
      if (cls.days && cls.days.includes(todayStr)) {
        result.push({
          student: student.name,
          code: cls.code,
          name: cls.name,
          instructor: cls.instructor,
          location: cls.location,
          time: cls.time,
        });
      }
    });
  });
  return result;
}

